Command.SQL:
create trigger "${schema}".${QB}${triggerName}${QE}
  ${triggerTime} ${triggerEvent}
  on "${schema}"."${objectname}"${_references}
  for each ${triggerType} mode db2sql
  ${_when}
${source}


create sequence "${_schema}".${QB}${sequenceName}${QE}
  start with ${startWith}
  increment by ${increment}
  no maxvalue
  no minvalue
  ${cycle}


CREATE FUNCTION "${functionOwner}".${QB}${functionName}${QE}
${_parameters}
RETURNS ${returns}
LANGUAGE JAVA
PARAMETER STYLE JAVA
EXTERNAL NAME '${externalName}'


CREATE PROCEDURE "${procOwner}".${QB}${procName}${QE}
${_parameters}
LANGUAGE JAVA
PARAMETER STYLE JAVA
EXTERNAL NAME '${externalName}'
